class Solution {
    public boolean isPalindrome(int x) {
        if(x &lt; 0) return false;
        int y = 0;
        int xx = x;
        while(xx != 0){
            y = y * 10 + xx % 10;
            xx = xx / 10;
        }
        if(y == x){
            return true;
        }
        else {
            return false;
        }

    }
}

