最后遍历完如果栈非空说明不匹配。



class Solution {
    public boolean isValid(String s) {
        char z[] = new char[10005];
        int l = 0;
        for(int i = 0;i&lt;s.length();i++){
            if(s.charAt(i)=='(' || s.charAt(i)=='[' || s.charAt(i)=='{'){
                z[l++] = s.charAt(i);
            }else{
                if(l&lt;=0) return false;
                if((s.charAt(i)==')' &amp;&amp; z[l-1]!='(') || (s.charAt(i)==']' &amp;&amp; z[l-1]!='[') || (s.charAt(i)=='}' &amp;&amp; z[l-1]!='{')){
                    return false;
                }else l--;
            }
        }
        if(l!=0) return false;
        return true;
    }
}
