class Solution {
    public ListNode reverseKGroup(ListNode head, int k) {
        if(k==1) return head;
        int len =0;
        ListNode n = head;
        while(n!=null &amp;&amp; len &lt; k){
            n = n.next;
            len++;
        }
        if(len &lt; k) return head;
        else{
            ListNode l1 = head,l2 = head.next,l3 = head.next.next;
            l1.next = reverseKGroup(n,k);
            for(int i = 1;i&lt;k;i++){
                l2.next = l1;
                l1 = l2;
                l2 = l3;
                if(l3!=null)
                l3 =l3.next;
            }
            return l1; 

        }

    }
}
