class Solution {
    public int[] searchRange(int[] nums, int target) {
        if(nums.length==0) return new int[]{-1,-1};
        int l = 0,r = nums.length-1;
        int mid;
        int ansl = -1,ansr = -1;
        while(l&lt;r){
            mid = (l+r)&gt;&gt;1;
            if(nums[mid]&lt;target) l = mid+1;
            else r = mid;
        }
        ansl = l;
        l = 0;
        r = nums.length-1;
        while(l&lt;r){
            mid = (l+r+1)&gt;&gt;1;
            if(nums[mid]&lt;=target) l = mid;
            else r = mid-1;
        }
        ansr = l;
        if(ansl&lt;=ansr &amp;&amp; ansl&lt;nums.length &amp;&amp; ansr&gt;=0 &amp;&amp; nums[ansl]==target &amp;&amp; nums[ansr]==target){
            return new int[]{ansl,ansr};
        }
        return new int[]{-1,-1};
    }
}
