class Solution {
    public List&lt;List&lt;Integer&gt;&gt; combinationSum(int[] candidates, int target){
        List&lt;List&lt;Integer&gt;&gt; ans = new ArrayList&lt;List&lt;Integer&gt;&gt;();
        ArrayList&lt;Integer&gt; ls = new ArrayList&lt;Integer&gt;();
        dfs(0,0,target,ls,ans,candidates);

        return ans;

    }
    private void dfs(int k,int sums,int target,List&lt;Integer&gt; ls,List&lt;List&lt;Integer&gt;&gt; ans,int[] candidates){
        if(sums == target) ans.add(new ArrayList&lt;Integer&gt;(ls));
        if(sums &gt; target) return ;
        for(int i = k; i&lt;candidates.length; i++){
            sums+=candidates[i];
            ls.add(candidates[i]);
            dfs(i,sums,target,ls,ans,candidates);
            sums -= candidates[i];
            ls.remove(ls.size() - 1);
        }
    }
}
