            if(height[i]&gt;l){
                System.out.println(""+sums+" "+full);
                full+=(i-li)*l;
                l = height[i];
                li = i;
            }
        }
        l = height[height.length-1];
        li = height.length-1;

        for(int i = height.length-1;i&gt;=heightest_ri;i--){
            sums+=height[i];
            if(height[i]&gt;l){
                full+=(li - i)*l;
                l = height[i];
                li = i;
            }

        }
        for(int i = heightest_li;i&lt;=heightest_ri;i++){
            sums+=height[i];
        }
        full+=(heightest_ri-heightest_li+1)*heightest;

        return full-sums + 2*heightest;
    }
}
