class Solution {
    public int lengthOfLongestSubstring(String s) {
        int a[] = new int[1000];
        int l = -1, r = 0;
        int maxl = 0;
        for(r = 0; r&lt; s.length();r++){
            a[(int)s.charAt(r)]+=1;     // è®¡æ•°
            if(a[(int)s.charAt(r)]&lt;=1){ // ä¸é‡å¤æ›´æ–°ç­”æ¡ˆ
                maxl = maxl&gt; (r-l) ? maxl: r-l; 
            }else{                      //é‡å¤å·¦æŒ‡é’ˆå‘å³ç§»ï¼Œç›´åˆ°ä¸é‡å¤
                while(a[(int)s.charAt(r)]&gt;1){
                    l+=1;
                    a[(int)s.charAt(l)]-=1;
                }
            }
        }
        return maxl;
    }

}
// å“ˆå¸Œé›†åˆ
class Solution {
    public int lengthOfLongestSubstring(String s) {
        Set&lt;Character&gt; occ = new HashSet&lt;Character&gt;();
        int len = s.length();
        int l = -1, r = 0;
        int maxl = 0;
        for(r = 0; r&lt; s.length();r++){
            while(occ.contains(s.charAt(r))) {
                occ.remove(s.charAt(++l));
            }
            occ.add(s.charAt(r));
            maxl = Math.max(maxl,r-l);
        }
        return maxl;
    }

}

