        } else {
            otherOut.writeBytes(log + "\n");
        }
    }

    @Override
    public void close(TaskAttemptContext context) throws IOException, InterruptedException {
        //å…³æµ
        IOUtils.closeStream(atguiguOut);
        IOUtils.closeStream(otherOut);
