class Solution {
    public int lengthOfLongestSubstring(String s) {
        Set&lt;Character&gt; occ = new HashSet&lt;Character&gt;();
        int len = s.length();
        int l = -1, r = 0;
        int maxl = 0;
        for(r = 0; r&lt; s.length();r++){
            while(occ.contains(s.charAt(r))) {
                occ.remove(s.charAt(++l));
            }
            occ.add(s.charAt(r));
            maxl = Math.max(maxl,r-l);
        }
        return maxl;
    }

}









