class Solution {
    public double findMedianSortedArrays(int[] nums1, int[] nums2) {
        int l1 = nums1.length, l2 = nums2.length;
        int[] nums = new int[l1+l2];
        for(int i = 0;i &lt; l1; i++) {
            nums[i] = nums1[i];
        }
        for(int i = 0; i &lt; l2; i++) {
            nums[l1+i] = nums2[i];
        }
        Arrays.sort(nums);

        if((l1 + l2) % 2 == 1) {
            return (double)nums[(l1 + l2) / 2];
        }else{
            return (double)(nums[(l1 + l2) / 2]+nums[(l1 + l2) / 2 - 1]) / 2.0;
        }
    }
}







