class Solution {
    public double findMedianSortedArrays(int[] nums1, int[] nums2) {
        int l1 = nums1.length, l2 = nums2.length;
        if((l1 + l2) % 2 == 1) {
            return findk(nums1,nums2,0,0,(l1+l2)/2+1);
        }else{
            return (findk(nums1,nums2,0,0,(l1+l2)/2)+findk(nums1,nums2,0,0,(l1+l2)/2+1)) / 2.0;
        }
    }
    private double findk(int[] nums1,int[] nums2,int i,int j,int k){
        //è¾¹ç•Œæƒ…å†µ
        if(i == nums1.length) return (double)nums2[j+k-1];
        if(j == nums2.length) return (double)nums1[i+k-1];
        if(k==1) return Math.min(nums1[i],nums2[j]);
        //æ ¹æ®æƒ…å†µå»é€’å½’ä¸‹å»
        int x = Math.min(nums1.length - 1, i+k/2 - 1);
        int y = Math.min(nums2.length - 1, j+k/2 - 1);
        if(nums1[x] &lt; nums2[y]) return findk(nums1,nums2,x+1,j,k - (x - i) - 1);
        else return findk(nums1,nums2,i,y+1,k - (y - j) - 1);
    }
}
