class Solution {
    public String longestPalindrome(String s) {
        int len = s.length();
        int maxl = 0;
        String ans = "";
        for (int i = 0; i &lt; len; i++){
            int j = 0;
            while(i-j&gt;=0 &amp;&amp; i+j &lt; len &amp;&amp; s.charAt(i-j) == s.charAt(i+j)){
                if(2*j+1&gt;maxl){
                    maxl=2*j+1;
                    ans = s.substring(i-j,i+j+1);
                }
                j++;
            }
             j = 0;
            while(i-j&gt;=0 &amp;&amp; i+j+1 &lt; len &amp;&amp; s.charAt(i-j) == s.charAt(i+j+1)){
                if(2*(j+1)&gt;maxl){
                    maxl=2*(j+1);
                    ans = s.substring(i-j,i+j+2);
                }
                j++;
            }
        }
        return ans;
    }
}
