class Solution {
    public int reverse(int x) {
        int ans = 0;
        while(x !=0 ) {
            if( x &lt; 0 &amp;&amp; ans &lt; (-Math.pow(2,31) - x %10) / 10) {
                return 0;
            }
            if( x &gt; 0 &amp;&amp; ans &gt; (Math.pow(2,31) - x %10 -1) / 10){
                return 0;
            }
            ans = ans * 10 + x % 10;
            x = x / 10;
        }
        return ans;
