class Solution {
    public int myAtoi(String s) {
        int l = 0;
        int f = 1;
        while(l &lt; s.length() &amp;&amp; s.charAt(l) == ' ') {
            l++;
        }
        if(l &lt; s.length() &amp;&amp; s.charAt(l)=='-'){
            f = -1;
            l++;
        }else if( l &lt; s.length() &amp;&amp; s.charAt(l)=='+' ){
            l++;
        }
        int ans = 0;
        while(l &lt; s.length() &amp;&amp; (s.charAt(l)&gt;='0' &amp;&amp; s.charAt(l)&lt;='9')){
            int x = (int)s.charAt(l)-(int)'0';
            if(f==-1 &amp;&amp; f*ans&lt;(-Math.pow(2,31)+x)/10) return -(int)Math.pow(-2,31);
            if(f==1 &amp;&amp; ans&gt;(Math.pow(2,31)-x-1)/10) return (int)(Math.pow(2,31) - 1);
            ans = ans*10 + x;
            l++;
        }

        return f*ans;
    }
}
