class Solution {
    public String intToRoman(int num) {
        int[] nums = {1000, 900, 500, 400, 100, 90, 50, 40, 10, 9, 5, 4, 1};
        String[] s = {"M", "CM", "D", "CD", "C", "XC", "L", "XL", "X", "IX", "V", "IV", "I"};
        int l = 0;
        String ans = "";
        while(l &lt; nums.length){
            while(num&gt;=nums[l]){
                num -= nums[l];
                ans += s[l];
            }
            l++;
        }
        return ans;

    }
}
