class Solution {
    public List&lt;List&lt;Integer&gt;&gt; fourSum(int[] nums, int target) {
        Arrays.sort(nums);
        List&lt;List&lt;Integer&gt;&gt; ans = new ArrayList&lt;List&lt;Integer&gt;&gt;();
        for(int i = 0;i&lt;nums.length;i++){
            if(i&gt;0 &amp;&amp; nums[i]==nums[i-1]) continue;
            for(int j = i+1;j&lt;nums.length;j++){
                if(j&gt;i+1 &amp;&amp; nums[j]==nums[j-1]) continue;
                int l = j+1,r = nums.length-1,t = target-(nums[i]+nums[j]);
                while(l&lt;r){
                    if(nums[l]+nums[r]&gt;t) r--;
                    else if(nums[l]+nums[r]&lt;t) l++;
                    else{
                        List&lt;Integer&gt; new_ans = new ArrayList&lt;Integer&gt;();
                        new_ans.add(nums[i]);
                        new_ans.add(nums[j]);
                        new_ans.add(nums[l]);
                        new_ans.add(nums[r]);
                        ans.add(new_ans);
                        l++;r--;
                        while(l&lt;nums.length &amp;&amp; nums[l]==nums[l-1]) l++;
                        while(r&gt;j &amp;&amp; nums[r]==nums[r+1]) r--;

                    }
                }
            }
        }
        return ans;
    }
}
