class Solution {
    public ListNode mergeTwoLists(ListNode l1, ListNode l2) {
        ListNode ans = new ListNode();
        ListNode a = ans;
        while(l1 != null &amp;&amp; l2 !=null){
            if(l1.val&lt;l2.val){
                a.next = l1;
                a = l1;
                l1 = l1.next;
            }else{
                a.next = l2;
                a = l2;
                l2 = l2.next;
            }
        }
        if(l1 != null) a.next = l1;
        if(l2 != null) a.next = l2;
        return ans.next;

    }
}








