class Solution {
    public ListNode mergeTwoLists(ListNode l1, ListNode l2) {
        if(l1 == null) return l2;
        if(l2 == null) return l1;
        if(l1.val &lt; l2.val){
            ListNode a = l1;
            a.next = mergeTwoLists(l1.next,l2);
            return a;
        }else{
            ListNode a = l2;
            a.next = mergeTwoLists(l2.next,l1);
            return a;
        }

    }
}
