class Solution {
    public int strStr(String haystack, String needle) {
        int l2 = needle.length(),l1 = haystack.length();
        if(l2==0) return 0;
        if(l1&lt;l2) return -1;
        for(int i = 0;i&lt;=l1-l2;i++){
            int f = 1;
            for(int j = 0;j&lt;l2;j++){
                if(haystack.charAt(i+j)!=needle.charAt(j)){
                    f = 0;
                    break;
                }
            }
            if(f==1){
                return i;
            }
        }
        return -1;

    }
}










