class Solution {
    public int divide(int dividend, int divisor) {
        int f1=1,f2=1,ans=0;
        if(divisor==dividend) return 1;
        if(divisor==-2147483648) return 0;

        if(dividend==-Math.pow(2,31) &amp;&amp; divisor==-1){
            return (int)(Math.pow(2,31)-1);
        }
        if(dividend&lt;0){
            f1 = -1;
        }
        if(divisor&lt;0){
            f2 = -1;
        }
        int num = 1;
        while(Math.abs(divisor)&lt;Math.pow(2,28)){
            divisor = divisor + divisor;
            num = num +num;
        }
        while(num !=0){
            System.out.println(""+num+" "+divisor+" "+dividend);
            while(f1*(dividend-f1*f2*divisor)&gt;=0){
                dividend-=f1*f2*divisor;
                ans+=num;
            }
            num = num&gt;&gt;1;
            if(f2==-1) divisor = -divisor;
            divisor = divisor&gt;&gt;1;
            if(f2==-1) divisor = -divisor;

        }
        return ans*f1*f2;

    }
}

















